INSERT INTO `tbl_tintuc` (`MaTT`, `NgayNhap`, `AnhTinTuc`, `TieuDe`, `NoiDungTT`, `TrangThai`) VALUES
(4, '2019-12-15', './images/anhtintuc1.jpg', 'Giá xe Vario 150 mới nhất hôm nay tháng 12/2019 tại đại lý Việt Nam.', '<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Gi&aacute; xe Vario 150&nbsp;mới nhất h&ocirc;m nay th&aacute;ng 12/2019 tại đại l&yacute; Việt Nam:</strong>&nbsp;<strong>Honda Vario 150 2019</strong>&nbsp;với phi&ecirc;n bản ho&agrave;n to&agrave;n mới vừa được&nbsp;Astra Honda Motor (AHM) ra mắt&nbsp;với những thiết kế g&oacute;c cạnh sắc sảo tạo ra vẻ hầm hố v&agrave; thể thao cho chiếc xe đ&atilde; khiến cho c&aacute;c kh&aacute;ch h&agrave;ng nam v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;.&nbsp;<strong>Vario 150&nbsp;mới 2019 trang bị Smartkey</strong>&nbsp;được&nbsp;coi l&agrave; phi&ecirc;n bản thay thế ho&agrave;n to&agrave;n cho mẫu xe thế hệ cũ với thiết kế được thay đổi đ&aacute;ng kể tr&ocirc;ng thể thao hơn đi k&egrave;m h&agrave;ng loạt c&aacute;c trang bị c&ocirc;ng nghệ hiện đại mang hơi hướng tương lai, động cơ xe tay ga&nbsp;<strong>Vario 150&nbsp;đời mới&nbsp;2019</strong>&nbsp;cũng đ&atilde; đươc tinh chỉnh lại cho ph&ugrave; hợp hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Gi&aacute; xe Vario 150&nbsp;mới nhất 2019&nbsp;</strong>tại đại l&yacute; tư nh&acirc;n&nbsp;Văn Thanh&nbsp;với gi&aacute; b&aacute;n&nbsp;54.500.000 VNĐ&nbsp;đ&atilde; c&oacute; VAT (chưa bao gồm ph&iacute; trước bạ v&agrave; ph&iacute; cấp&nbsp;biển số), Hotline tư vấn : 0387895020.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Li&ecirc;n hệ để c&oacute; gi&aacute; tốt nhất&nbsp;tại c&aacute;c&nbsp;địa chỉ chi nh&aacute;nh Xe m&aacute;y Văn Thanh: &nbsp;</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">309 Vườn L&agrave;i, Phường Ph&uacute; Thọ H&ograve;a, Quận T&acirc;n Ph&uacute;, TPHCM&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">6C Trần Quang Diệu, Phường 13, Quận 3, TPHCM</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">211 Đồng Khởi, Phường&nbsp;Tam Hiệp, Th&agrave;nh phố&nbsp;Bi&ecirc;n Ho&agrave;, Đồng Nai&nbsp;</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">*Lưu &yacute;:&nbsp;Khi kh&aacute;ch h&agrave;ng đến mua xe&nbsp;n&ecirc;n mang theo CMND v&agrave; hộ khẩu.&nbsp;Trung t&acirc;m&nbsp;Xe m&aacute;y Văn Thanh&nbsp;c&oacute; b&aacute;n trả g&oacute;p Vario 150&nbsp;thủ tục đơn giản, nhanh ch&oacute;ng 15 ph&uacute;t)&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">*Tham khảo th&ecirc;m :</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Giá xe vario 150 mới nhất hôm nay tháng 122019 tại đại lý việt nam - 1\" src=\"https://giaxe.2banh.vn/dataupload/news/images/1559285236-677db9a79ef547b1754d53d4fd720661.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Honda Vairo 150 m&agrave;u mới 2019 tại Xenhap2banh.vn</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda Vario 150&nbsp;thế hệ mới 2019</strong>&nbsp;ra mắt thị trường Indonesia. So với mẫu Vario&nbsp;thế hệ cũ v&agrave; Vario 150cc phi&ecirc;n bản mới nhất đ&atilde; c&oacute; những thay đổi đ&aacute;ng kinh ngạc, mẫu xe tay ga 150 ph&acirc;n khối c&oacute; thiết kế kh&aacute;c biệt kh&aacute; nhiều.&nbsp;<strong>Xe Vario 2019&nbsp;</strong>l&agrave; mẫu xe m&agrave; Honda sản xuất d&agrave;nh ri&ecirc;ng cho thị trường Indonesia, với thiết kế được chia sẻ từ đ&agrave;n anh PCX. Ở phi&ecirc;n bản mới, Vario 150 c&oacute; những n&eacute;t thiết kế lấy từ người anh lớn&nbsp;CBR1000RR với&nbsp;những đường n&eacute;t g&oacute;c cạnh&nbsp;v&agrave; hầm hố c&ugrave;ng một khối động cơ mạnh mẽ hơn.&nbsp;<strong>Vario 150 mới 2019</strong>&nbsp;t&iacute;nh tới thởi điểm hiện nay vẫn được xem l&agrave; mẫu xe d&agrave;nh cho mọi lứa tuỗi kh&ocirc;ng ph&acirc;n biệt tuổi t&aacute;c.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda Vario 150 mới 2019</strong>&nbsp;c&oacute; thiết kế tương đồng với kiểu d&aacute;ng của phi&ecirc;n bản Vario 125 (chỉ kh&aacute;c biệt ở cặp m&acirc;m khi bản 125cc vẩn sử dụng thiết kế m&acirc;m cũ). Với những thiết kế thay đổi kh&aacute; ấn tượng, dự đo&aacute;n đ&acirc;y vẫn sẽ&nbsp;l&agrave; mẫu xe được nhiều anh em ưa chuộng tại thị trường Việt nam, đặc biệt hiện nay&nbsp;<strong>gi&aacute; xe Vario 150 Smartkey</strong>&nbsp;tại&nbsp;<strong>Xenhap2banh.vn</strong>&nbsp;được coi l&agrave; c&oacute; gi&aacute; b&aacute;n kh&aacute; tốt&nbsp;so thới điểm mới ra mắt.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhằm gi&uacute;p bạn đọc c&oacute; những th&ocirc;ng tin ch&iacute;nh x&aacute;c về&nbsp;gi&aacute; xe Vario 150 mới nhất h&ocirc;m nay 2019, c&aacute;c th&ocirc;ng tin đ&aacute;nh gi&aacute; chi tiết cũng như gi&uacute;p c&aacute;c bạn c&oacute; thể so s&aacute;nh gi&aacute; giữa c&aacute;c&nbsp;thời điểm mua xe th&iacute;ch hợp nhất cho bản th&acirc;n m&igrave;nh. N&agrave;o h&atilde;y c&ugrave;ng&nbsp;<strong>XeNhap2banh.vn</strong>&nbsp;tham khảo&nbsp;bảng&nbsp;<strong>gi&aacute; xe Vario 150 mới nhất th&aacute;ng 12/2019</strong>&nbsp;với những th&ocirc;ng tin đang hot b&ecirc;n dưới đ&acirc;y nh&eacute;!</span></span></p>\r\n', 1),
(5, '2019-12-15', './images/anhtintuc2.jpg', 'Giá xe SH 2019 mới nhất hôm nay tháng 12/2019 tại các đại lý Honda', '<p><strong>Gi&aacute; xe SH mới nhất</strong><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong> h&ocirc;m nay th&aacute;ng 12/2019 tại đại l&yacute; Honda</strong>:&nbsp;Trong những ng&agrave;y đầu th&aacute;ng 12/2019, b&ecirc;n cạnh mẫu xe&nbsp;<strong>Honda SH 2019</strong>&nbsp;đang b&aacute;n tại thị trường Việt Nam th&igrave; Honda đ&atilde; ch&iacute;nh thức giới thiệu mẫu&nbsp;<strong>SH 2020 phi&ecirc;n bản mới</strong>&nbsp;nhất với những thay đổi cả b&ecirc;n trong lẫn b&ecirc;n ngo&agrave;i&nbsp;đầy c&aacute; t&iacute;nh v&agrave;&nbsp;gi&aacute; xe SH 2019 h&ocirc;m nay<strong>&nbsp;</strong>c&oacute; kh&aacute; nhiều biến động đối với 2 d&ograve;ng xe SH150i v&agrave; SH125i mới ra mắt n&agrave;y, gi&aacute; xe tại c&aacute;c đại l&yacute; ở từng khu vực dao động tăng từ 9.4 &ndash; 21.2 triệu đồng. Từ l&uacute;c ch&iacute;nh thức được b&aacute;n ra tại thị trường Việt Nam,&nbsp;<strong>gi&aacute; xe SH 2019</strong>&nbsp;tại c&aacute;c cửa h&agrave;ng HEAD ở từng khu vực c&oacute; mức ch&ecirc;nh lệch gi&aacute; kh&aacute; kh&aacute;c nhau so với mức gi&aacute; đề xuất m&agrave; h&atilde;ng c&ocirc;ng bố t&ugrave;y theo phi&ecirc;n bản, ph&acirc;n khối v&agrave; độ Hot của n&oacute;.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Giá xe sh 2019 mới nhất hôm nay tháng 122019 tại các đại lý honda - 1\" src=\"https://giaxe.2banh.vn/dataupload/news/images/1572942316-4305bf1acdbd4dd934bc591f30f9aedd.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Honda SH 2020 phi&ecirc;n bản&nbsp;mới&nbsp;nhất vừa ra mắt</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Mẫu xe tay ga&nbsp;<strong>Honda SH&nbsp;</strong>với lịch sử&nbsp;bắt đầu c&oacute; mặt v&agrave; được người ti&ecirc;u d&ugrave;ng biết đến đầu tiền ở thị trường ch&acirc;u &Acirc;u v&agrave;o những năm 1984 với phi&ecirc;n bản 50 ph&acirc;n khối. Ở thế hệ sau đ&oacute;, Honda bổ sung th&ecirc;m c&aacute;c phi&ecirc;n bản SH 100, SH 125cc/150cc, SH 125i/150i v&agrave; SH 300i. Tại thị trường Việt Nam, d&ograve;ng xe SH125i/150i được giới thiệu v&agrave;o năm 2009. Sau đ&oacute;, mẫu xe được cải tiến với một số thay đổi nhỏ v&agrave;o năm 2010 v&agrave; 2011. Năm 2012 l&agrave; phi&ecirc;n bản SH125i/150i ho&agrave;n to&agrave;n mới. Phi&ecirc;n bản n&acirc;ng cấp trang bị&nbsp;hệ thống ch&igrave;a kh&oacute;a th&ocirc;ng minh, phi&ecirc;n bản SH 2019 mang kiểu d&aacute;ng hiện đại v&agrave; rất được người ti&ecirc;u d&ugrave;ng ưa chuộng. V&agrave; gần đ&acirc;y nhất l&agrave; phi&ecirc;n bản&nbsp;<strong>SH 2020</strong>&nbsp;với nhưng thay đổi mang đậm phong c&aacute;ch thiết kế hiện đại, tổng thể xe thay đổi ho&agrave;n to&agrave;n so với thế hệ cũ., tiện nghi hơn, trang bị an to&agrave;n ti&ecirc;n tiến c&ugrave;ng khả năng vận h&agrave;nh đ&atilde; được cải thiện đ&aacute;ng kể.</span></span></p>\r\n\r\n<h2><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; xe SH h&ocirc;m nay&nbsp;mới nhất th&aacute;ng 12/2019 tại c&aacute;c đại l&yacute; Honda HEAD:</span></span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda</strong>&nbsp;l&agrave; thương hiệu xe m&aacute;y Nhật Bản đ&atilde; chiếm lĩnh thị trường Việt Nam trong h&agrave;ng chục năm qua. Xe m&aacute;y Honda nổi tiếng với sự vận h&agrave;nh bền bỉ, tiết kiệm nhi&ecirc;n liệu, chi ph&iacute; bảo dưỡng v&agrave;&nbsp;sửa chữa&nbsp;rẻ hơn c&aacute;c d&ograve;ng xe m&aacute;y kh&aacute;c. V&agrave; một điều quan trọng ai cũng phải thừa nhận v&agrave; quan t&acirc;m l&agrave; xe m&aacute;y Honda b&aacute;n lại &ldquo;được gi&aacute;&rdquo;. Tuy nhi&ecirc;n, Honda cũng l&agrave; h&atilde;ng g&acirc;y &ldquo;kh&oacute; chịu&rdquo; nhất về gi&aacute; b&aacute;n khi gi&aacute; b&aacute;n lẻ thực tế tại c&aacute;c đại l&yacute; lu&ocirc;n &ldquo;ch&ecirc;nh&rdquo; cao hơn gi&aacute; b&aacute;n đề xuất kh&aacute; nhiều khiến nhiều người mua tỏ ra bức x&uacute;c.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda SH 2020</strong>&nbsp;hiện đang l&agrave; mẫu xe tay ga cao cấp đ&atilde; v&agrave; đang thu h&uacute;t được rất nhiều sự quan t&acirc;m của người ti&ecirc;u d&ugrave;ng, SH 2020 hướng đến đối tượng kh&aacute;ch h&agrave;ng tiềm năng l&agrave; những bạn trẻ th&agrave;nh đạt cho đến những người trung ni&ecirc;n lớn tuổi. Với lợi thế về số lượng kh&aacute;ch h&agrave;ng trung th&agrave;nh v&agrave; tin d&ugrave;ng d&ograve;ng sản phẩm n&agrave;y,&nbsp;<strong>Honda SH 2020</strong>&nbsp;đang được nhiều người dự đo&aacute;n sẽ tiếp độc chiếm ng&ocirc;i vương trong ph&acirc;n kh&uacute;c xe tay ga cao cấp tại thị trường Việt Nam. Mẫu SH 2020 với 2 phi&ecirc;n bản 150cc v&agrave; 125cc được trang bị hệ thống ch&igrave;a th&ocirc;ng minh Smart key, hệ thống đ&egrave;n full LED, đồng hồ Full LCD, cơ chế ngắt động cơ tạm thời Idling Stop, ở cả 2 phi&ecirc;n bản đều c&oacute; thể t&ugrave;y chọn hệ thống phanh ABS 2 k&ecirc;nh&nbsp;hoặc CBS truyền thống (ABS mắc hơn 8 triệu đồng)</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo c&ocirc;ng bố mẫu xe&nbsp;<strong>Honda SH 2020</strong>&nbsp;mới ra mắt sẽ&nbsp;được h&atilde;ng Honda Việt Nam sẽ được ch&iacute;nh thức b&aacute;n ra thị trường v&agrave;o ng&agrave;y 24 th&aacute;ng 11 năm 2019 th&ocirc;ng qua c&aacute;c cửa h&agrave;ng B&aacute;n xe v&agrave; Dịch vụ do Honda Ủy nhiệm (HEAD) tr&ecirc;n to&agrave;n quốc với thời gian bảo h&agrave;nh 3 năm hoặc 30.000 km (t&ugrave;y theo điều kiện n&agrave;o đến trước) với 4 m&agrave;u sắc Đen - Trắng Đen - X&aacute;m Đen - Đỏ Đen cho cả 2 phi&ecirc;n bản động cơ Honda SH 125i/150i 2020. Hiện&nbsp;<strong><u><a href=\"https://giaxe.2banh.vn/honda/sh-2020.html\">Gi&aacute; xe SH 2020</a></u>&nbsp;tại đại l&yacute;</strong>&nbsp;theo c&ocirc;ng bố chỉ mới c&oacute; gi&aacute; đề xuất chứ vẫn chưa c&oacute; gi&aacute; lăn b&aacute;nh tại đại l&yacute;, c&ugrave;ng tham khảo nh&eacute;:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">- Gi&aacute; xe SH 150i phi&ecirc;n bản phanh ABS: 95.990.000 VNĐ</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">- Gi&aacute; xe SH 150i phi&ecirc;n bản phanh CBS: 87.990.000 VNĐ</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">- Gi&aacute; xe SH 125i phi&ecirc;n bản phanh ABS: 78.990.000 VNĐ</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">- Gi&aacute; xe SH 125i phi&ecirc;n bản phanh CBS: 70.990.000 VNĐ</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1),
(6, '2019-12-15', './images/anhtintuc3.jpg', 'Giá xe Vario 125 mới nhất hôm nay tháng 12/2019 tại đại lý Việt Nam', '<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Gi&aacute; xe Vario 125&nbsp;mới nhất h&ocirc;m nay th&aacute;ng 12/2019:</strong>&nbsp;<strong>Honda Vario 125</strong>&nbsp;xứng đ&aacute;ng l&agrave; niềm tự h&agrave;o của Honda trong năm 2019 n&agrave;y, l&agrave; sự lựa chọn mới trong ph&acirc;n kh&uacute;c xe ga tại thị trường Việt Nam bởi thiết kế mẫu m&atilde; bắt mắt, m&agrave;u sắc trẻ trung năng động, hợp với cả nam lẫn nữ.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Giá xe vario 125 mới nhất hôm nay tháng 122019 tại đại lý việt nam - 1\" src=\"https://giaxe.2banh.vn/dataupload/news/images/1557223278-fb4aabc77e78da1aef76fcddcad80e5d.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Xe tay ga Honda Vario 125 2019 v&agrave;ng c&aacute;t đang được b&aacute;n tại Xenhap2banh.vn</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda Vario 125</strong>&nbsp;l&agrave; phi&ecirc;n bản mới nhất vừa được ra mắt của mẫu&nbsp;<strong>xe tay ga Vario</strong>&nbsp;được sản xuất v&agrave; d&agrave;nh ri&ecirc;ng cho thị trường Indonesia,&nbsp;<strong>Xe Vario 125 2019</strong>&nbsp;ở phi&ecirc;n bản n&agrave;y vẫn giữ&nbsp;nguy&ecirc;n kiểu d&aacute;ng g&oacute;c cạnh hầm hố, mạnh mẽ từ đ&agrave;n anh chỉ kh&aacute;c biệt về dung t&iacute;ch động cơ.&nbsp;<a href=\"https://giaxe.2banh.vn/honda/vario-2018.html\"><strong>Vario&nbsp;2019</strong></a>&nbsp;được nhập khẩu trực tiếp từ Indonesia về Việt Nam l&agrave; sự lựa chọn th&uacute; vị cho người ti&ecirc;u d&ugrave;ng trong nước v&agrave; được xem l&agrave; mẫu xe đối trọng mạnh mẽ với c&aacute;c d&ograve;ng xe tay ga tầm trung đang được b&aacute;n ch&iacute;nh h&atilde;ng tại Việt Nam.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Tuy nhi&ecirc;n&nbsp;<strong>gi&aacute; xe Vario 125 mới nhất 2019</strong>&nbsp;tại c&aacute;c đại l&yacute; lu&ocirc;n l&ecirc;n xuống thất thường, nhằm gi&uacute;p bạn đọc c&oacute; những th&ocirc;ng tin ch&iacute;nh x&aacute;c về&nbsp;<strong>gi&aacute; xe Vario 2019&nbsp;hiện nay</strong>, c&aacute;c th&ocirc;ng tin đ&aacute;nh gi&aacute; chi tiết cũng như gi&uacute;p c&aacute;c bạn c&oacute; thể so s&aacute;nh gi&aacute; giữa c&aacute;c&nbsp;thời điểm mua xe th&iacute;ch hợp nhất cho bản th&acirc;n m&igrave;nh. N&agrave;o h&atilde;y c&ugrave;ng&nbsp;<u>XeNhap2banh.vn</u>&nbsp;tham khảo&nbsp;<strong>bảng gi&aacute; xe Vario 125 mới nhất th&aacute;ng 12/2019</strong>&nbsp;với những th&ocirc;ng tin đang hot b&ecirc;n dưới đ&acirc;y nh&eacute;!</span></span></p>\r\n\r\n<h2><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Bảng gi&aacute; xe Vario 125 mới nhất th&aacute;ng 12/2019&nbsp;tại Xenhap2banh.vn</span></span></h2>\r\n\r\n<table cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Bảng gi&aacute; xe Vario 125 h&ocirc;m nay th&aacute;ng 12/2019 tại Xenhap2banh.vn</strong></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; chưa giấy</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; bao giấy</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; xe Vario 125 m&agrave;u Xanh nh&aacute;m</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">44.700.000</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">48.900.000</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; xe Vario 125 m&agrave;u Đen Nh&aacute;m</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">43.900.000</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">48.100.000</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; xe Vario 125 m&agrave;u V&agrave;ng C&aacute;t&nbsp;</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">44.700.000</span></span></td>\r\n			<td><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">48.900.000</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda Vario 125</strong>&nbsp;tương tự như người đ&agrave;n anh Vario 150 rất được ưa chuộng tại thị trường Việt Nam, mẫu xe nổi tiếng của h&atilde;ng Honda với những thiết kế g&oacute;c cạnh sắc xảo tạo ra vẻ hầm hố v&agrave; thể thao cho chiếc xe. Phần đầu xe ấn tượng với cặp đ&egrave;n LED chạy d&agrave;i rất hiện đại. Ch&iacute;nh v&igrave; đ&acirc;y l&agrave; mẫu xe thu h&uacute;t được rất nhiều kh&aacute;ch h&agrave;ng n&ecirc;n việc&nbsp;<strong>gi&aacute; xe Vario 125</strong>&nbsp;hiện nay lu&ocirc;n g&acirc;y ra kh&oacute; chịu khi cao hơn gi&aacute; thực tế.&nbsp;<strong>Gi&aacute; xe Vario 125 mới nhất th&aacute;ng 12/2019</strong>&nbsp;hiện nay tại&nbsp;<strong>Xenhap2banh.vn</strong>&nbsp;v&agrave;o khoảng&nbsp;48.100.000 đồng&nbsp;(đ&atilde; bao gồm ph&iacute; VAT, ph&iacute; thuế trước bạ + ph&iacute; ra biển số) t&ugrave;y v&agrave;o&nbsp;thời điểm.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Honda Vario 125</strong>&nbsp;được coi l&agrave; mẫu xe rất dễ tiếp cận tại thị trường Việt Nam bởi người ti&ecirc;u d&ugrave;ng đ&atilde; qu&aacute; quen thu&ocirc;c với phi&ecirc;n bản trước đ&oacute; l&agrave; Vario 150cc.&nbsp;<strong>Vario 125 mới 2019</strong>&nbsp;vẫn giữ nguy&ecirc;n thiết kế so với phi&ecirc;n bản 150cc&nbsp;chỉ thay đổi chỉnh sửa một số chi tiết như m&agrave;u s&aacute;c c&ugrave;ng họa tiết tem v&agrave; cặp m&acirc;m vẫn giữ nguy&ecirc;n so với đời củ,&nbsp;<u>Vario 125 2019</u>&nbsp;tập trung mang tới n&eacute;t hiện đại v&agrave; c&aacute; t&iacute;nh hơn. Vario 125 nhỏ gọn để chạy tr&ecirc;n đường phố đ&ocirc;ng đ&uacute;c, kiểu d&aacute;ng tổng thể cơ bản sẽ c&oacute; phần đầu th&aacute;p, d&agrave;n đu&ocirc;i cao rất thể thao v&agrave; được b&aacute;n tại thị trường&nbsp;Việt Nam với 6 m&agrave;u :</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Vario 125 mới 2019 phi&ecirc;n bản thể thao m&agrave;u Đỏ&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Vario 125 2019 phi&ecirc;n bản thể thao m&agrave;u Trắng&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Vario 125 mới 2019 phi&ecirc;n bản thể thao m&agrave;u X&aacute;m Đen</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Vario 125 mới 2019 phi&ecirc;n bản thể thao m&agrave;u Đen Nh&aacute;m</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Vario 125 mới 2019 phi&ecirc;n bản thể thao m&agrave;u Xanh Nh&aacute;m</span></span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Vario 125 mới 2019 phi&ecirc;n bản thể thao m&agrave;u V&agrave;ng C&aacute;t</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Giá xe vario 125 mới nhất hôm nay tháng 122019 tại đại lý việt nam - 3\" src=\"https://giaxe.2banh.vn/dataupload/news/images/1557377405-fbb24dac71c2dc92c9fec615a1a5d52e.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Vario 125 m&agrave;u xanh nh&aacute;m&nbsp;mới 2019</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>Gi&aacute; xe Vario 125 mới nhất th&aacute;ng 12/2019&nbsp;tại Xenhap2banh.vn</strong></u>&nbsp;: Theo t&igrave;m hiểu v&agrave; ghi nhận một v&ograve;ng thực tế tại c&aacute;c đại l&yacute; tư nh&acirc;n lớn th&igrave;&nbsp;<strong>gi&aacute; xe Vario 125 mới nhấ</strong>t&nbsp;tại&nbsp;Việt Nam ở khu vực H&agrave; Nội v&agrave; TP. Hồ Ch&iacute; Minh gi&aacute; xe vario 125 c&oacute; gi&aacute; b&aacute;n giảm mạnh&nbsp;ở tất cả c&aacute;c m&agrave;u xe so với gi&aacute; thời điểm l&uacute;c mới ra mắt.&nbsp;Nh&igrave;n chung mức&nbsp;<u>gi&aacute; xe Vario 125</u>&nbsp;nhập khẩu với kiểu d&aacute;ng thể thao c&aacute; t&iacute;nh c&ugrave;ng những c&ocirc;ng nghệ hiện đại nếu so với gi&aacute; những mẫu xe tay ga tầm trung trong nước th&igrave; hiện nay Vario 125&nbsp;thời điểm n&agrave;y c&oacute;&nbsp;mức gi&aacute; hợp l&yacute; c&oacute; thể chấp nhận được.</span></span></p>\r\n', 1),
(17, '2019-12-15', './images/anhtintuc4.jpg', 'Giá xe Benelli VZ125i mới nhất hôm nay tháng 12/2019 tại đại lý Việt Nam', '<p><strong>Gi&aacute; xe Benelli VZ125i mới nhất&nbsp;h&ocirc;m nay th&aacute;ng 12/2019 tại Việt Nam</strong>: Vừa qua mẫu xe tay ga&nbsp;<strong>Benelli VZ125i 2020</strong>&nbsp;nhập khẩu trực tiếp Malaysia thuộc ph&acirc;n kh&uacute;c tầm trung ch&iacute;nh thức được b&aacute;n ch&iacute;nh h&atilde;ng tại Việt Nam.&nbsp;<strong>Gi&aacute; xe Benelli VZ125i mới nhất th&aacute;ng 12</strong>&nbsp;c&oacute; gi&aacute; b&aacute;n đề xuất cực kỳ cạnh tranh so với c&aacute;c d&ograve;ng xe kh&aacute;c trong c&ugrave;ng ph&acirc;n kh&uacute;c đi c&ugrave;ng thiết kế trẻ trung, c&aacute; t&iacute;nh, khối động cơ mạnh mẽ, vận h&agrave;nh linh hoạt hướng tới đối tượng kh&aacute;ch h&agrave;ng trẻ, năng động đ&atilde; thu h&uacute;t rất nhiều sự ch&uacute; &yacute; của người ti&ecirc;u d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"Giá xe benelli vz125i mới nhất hôm nay tháng 122019 tại đại lý việt nam - 1\" src=\"https://giaxe.2banh.vn/dataupload/news/images/1570522821-fc71f5f7c3578bd80027e5cf607516b1.jpg\" /></p>\r\n\r\n<p><em>Gi&aacute; xe Benelli VZ125i mới nhất h&ocirc;m nay 2020 tại khu vực TP.HCM</em></p>\r\n\r\n<p><strong>Benelli VZ125i mới nhất 2020</strong>&nbsp;l&agrave; mẫu xe tay ga được h&atilde;ng xe &Yacute; ph&aacute;t triển d&agrave;nh ri&ecirc;ng cho thị trường ch&acirc;u &Aacute; với thiết kế tổng thể nhỏ gọn, tem xe hiện đại mới mẻ, động cơ mạnh mẽ linh hoạt, tiết kiệm nhi&ecirc;n liệu, t&iacute;ch hợp c&aacute;c tiện &iacute;ch cho d&ograve;ng xe đ&ocirc; thị rất c&oacute; sức hấp dẫn tại thị trường Malay. Ch&iacute;nh v&igrave; vậy,&nbsp;<strong>gi&aacute; xe Benelli VZ125i mới nhất th&aacute;ng 12/2019</strong>&nbsp;khi về tới Việt Nam hiện đang được người ti&ecirc;u d&ugrave;ng rất quan t&acirc;m khi gi&aacute; b&aacute;n ch&iacute;nh thức rẻ hơn cả chục triệu đồng so với c&aacute;c d&ograve;ng xe tay ga c&ugrave;ng ph&acirc;n kh&uacute;c hiện tại.</p>\r\n\r\n<p><strong>Gi&aacute; xe Benelli 2020 tại đại l&yacute; th&aacute;ng 12</strong>&nbsp;sau khi vừa ch&iacute;nh thức b&aacute;n ra tại Việt Nam hiện đang c&oacute; mức gi&aacute; cực kỳ cạnh tranh v&agrave; hầu như chưa xuất hiện t&igrave;nh trạng k&ecirc; cao gi&aacute; hứa hẹn đem tới luồng gi&oacute; mới cho ph&acirc;n kh&uacute;c xe tay ga phổ th&ocirc;ng. &quot;<u>Gi&aacute; xe Benelli VZ125i h&ocirc;m nay gi&aacute; bao nhi&ecirc;u</u>&quot; hiện rất được người d&ugrave;ng quan t&acirc;m bởi do mẫu xe tay ga của h&atilde;ng Benelli chỉ vừa b&aacute;n ra tại Việt Nam n&ecirc;n người d&ugrave;ng vẫn chưa c&oacute; nhiều th&ocirc;ng tin. Gi&aacute; xe tay ga Benelli VZ125i 2020 thực tế khi tới tay người ti&ecirc;u d&ugrave;ng sẽ c&oacute; gi&aacute; b&aacute;n dao động với mức gi&aacute; đề xuất m&agrave; h&atilde;ng c&ocirc;ng bố t&ugrave;y theo khu vực v&agrave; thời điểm mua xe.</p>\r\n\r\n<p><img alt=\"Giá xe benelli vz125i mới nhất hôm nay tháng 122019 tại đại lý việt nam - 2\" src=\"https://giaxe.2banh.vn/dataupload/news/images/1570522822-4b49961d6bb5e51cccf4961a5df11e16.jpg\" /></p>\r\n\r\n<p>Mẫu&nbsp;<strong>xe tay ga Benelli VZ125i</strong>&nbsp;mới 2020 được ph&acirc;n phối th&ocirc;ng qua c&aacute;c đại l&yacute; Benelli ch&iacute;nh thức chỉ với 1 phi&ecirc;n bản trang bị b&igrave;nh dầu phuộc sau Kayaba chứ kh&ocirc;ng như tại thị trường Malay chia l&agrave;m 2 phi&ecirc;n bản kh&aacute;c nhau. Xe tay ga VZ125i mới nhất 2020 được coi l&agrave; sẽ cạnh tranh trực tiếp với mẫu xe tay ga như FreeGo, BeaT, AirBlade lẫn Honda Vision khi c&oacute; rất nhiều lợi thế về gi&aacute; b&aacute;n t&iacute;nh tới thời điểm hiện nay.</p>\r\n\r\n<p>T&iacute;nh tới thời điểm hiện tại mẫu xe&nbsp;<strong>Benelli VZ125i mới nhất 2020</strong>&nbsp;được ph&acirc;n phối ch&iacute;nh thức với 3 phi&ecirc;n bản m&agrave;u sắc kh&aacute;c nhau đi c&ugrave;ng khối động cơ 125cc đ&atilde; được tinh chỉnh lại đạt ti&ecirc;u chuẩn Euro3 gi&uacute;p xe c&oacute; khả năng ti&ecirc;u thụ nhi&ecirc;n liệu đạt mức tiết kiệm l&yacute; tưởng trong điều kiện vận h&agrave;nh đ&ocirc; thị đ&ocirc;ng đ&uacute;c.</p>\r\n', 1);

